import React from "react"
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import InputBase from "@material-ui/core/InputBase";
import Select from "@material-ui/core/Select";
import withStyles from "@material-ui/core/styles/withStyles";
import MenuItem from "@material-ui/core/MenuItem";
import Container from "@material-ui/core/Container";
import {makeStyles} from "@material-ui/core";
import Title from "../Title"
import Button from "@material-ui/core/Button";

const BootstrapInput = withStyles((theme) => ({
    root: {
        'label + &': {
            marginTop: theme.spacing(3),
        },
    },
    input: {
        borderRadius: 4,
        position: 'relative',
        backgroundColor: theme.palette.background.paper,
        border: '1px solid #ced4da',
        fontSize: 16,
        padding: '10px 26px 10px 12px',
        transition: theme.transitions.create(['border-color', 'box-shadow']),
        // Use the system font instead of the default Roboto font.
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:focus': {
            borderRadius: 4,
            borderColor: '#80bdff',
            boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
        },
    },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1)
    },
    addButton: {
        marginTop: theme.spacing(4)
    }
}))


function Accounts() {
    const [drcr, setdrcr] = React.useState("DR")
    const [type, setType] = React.useState("Asset")

    const AddingAccountForm = () => {
        const classes = useStyles();
        return (
            <Container>
                <FormControl className={classes.margin}>
                    <InputLabel>Account Name</InputLabel>
                    <BootstrapInput/>
                </FormControl>

                <FormControl className={classes.margin}>
                    <InputLabel>Type</InputLabel>
                    <Select
                        value={type}
                        input={<BootstrapInput/>}
                        onChange={(event) => {setType(event.target.value)}}
                    >
                        <MenuItem value="Asset">Asset</MenuItem>
                        <MenuItem value="Liability">Liability</MenuItem>
                        <MenuItem value="Equity">Equity</MenuItem>
                    </Select>
                </FormControl>

                <FormControl className={classes.margin}>
                    <InputLabel>DR/CR</InputLabel>
                    <Select
                        value={drcr}
                        input={<BootstrapInput/>}
                        onChange={(event) => {setdrcr(event.target.value)}}
                    >
                        <MenuItem value="DR">DR</MenuItem>
                        <MenuItem value="CR">CR</MenuItem>
                    </Select>
                </FormControl>

                <Button variant={"contained"} color={"primary"} className={classes.addButton}>+</Button>
            </Container>
        )
    }

    return (
        <div style={{width: "80%", margin: 20}}>
            <Title titleName={"Accounts"}/>
            {<AddingAccountForm />}
        </div>
    )
}

export default Accounts
