import React from "react"
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import InputBase from "@material-ui/core/InputBase";
import Select from "@material-ui/core/Select";
import withStyles from "@material-ui/core/styles/withStyles";
import TextField from "@material-ui/core/TextField";
import MenuItem from "@material-ui/core/MenuItem";
import Container from "@material-ui/core/Container";
import {makeStyles} from "@material-ui/core";
import Title from "../Title"
import Button from "@material-ui/core/Button";

const BootstrapInput = withStyles((theme) => ({
    root: {
        'label + &': {
            marginTop: theme.spacing(3),
        },
    },
    input: {
        borderRadius: 4,
        position: 'relative',
        backgroundColor: theme.palette.background.paper,
        border: '1px solid #ced4da',
        fontSize: 16,
        padding: '10px 26px 10px 12px',
        transition: theme.transitions.create(['border-color', 'box-shadow']),
        // Use the system font instead of the default Roboto font.
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:focus': {
            borderRadius: 4,
            borderColor: '#80bdff',
            boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',
        },
    },
}))(InputBase);

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1)
    },
    addButton: {
        marginTop: theme.spacing(4),
        marginLeft: theme.spacing(5),
        fontSize: 25,
        height: 41
    },
    container: {
        width: "100%"
    },
    input: {
        border: "1px solid #ced4da",
        padding: "10px 26px 10px 12px",
        position: "relative",
        fontSize: "16px",
        transition: "border-color 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms,box-shadow 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms",
        fontFamily: "-apple-system,BlinkMacSystemFont",
        borderRadius: "4px",
        backgroundColor: "#fff",
        marginTop: 25
    },
}))

const AddingAccountForm = ({accountName, setAccountName, type, setType, drcr, setdrcr, money, setMoney, setAccounts, accounts}) => {
    const classes = useStyles();
    return (
        <Container className={classes.container} key={"Container"}>
            <FormControl className={classes.margin}>
                <InputLabel style={{transform: "translate(0, 1.5px) scale(0.75)"}}>Account Name</InputLabel>
                <input className={classes.input} ref={(a) => accountName = a}/>
            </FormControl>

            <FormControl className={classes.margin}>
                <InputLabel>Type</InputLabel>
                <Select
                    value={type}
                    input={<BootstrapInput/>}
                    onChange={(event) => {
                        setType(event.target.value)
                    }}
                >
                    <MenuItem value="Asset">Asset</MenuItem>
                    <MenuItem value="Liability">Liability</MenuItem>
                    <MenuItem value="Equity">Equity</MenuItem>
                </Select>
            </FormControl>

            <FormControl className={classes.margin}>
                <InputLabel>DR/CR</InputLabel>
                <Select
                    value={drcr}
                    input={<BootstrapInput/>}
                    onChange={(event) => {
                        setdrcr(event.target.value)
                    }}
                >
                    <MenuItem value="DR">DR</MenuItem>
                    <MenuItem value="CR">CR</MenuItem>
                </Select>
            </FormControl>

            <FormControl className={classes.margin}>
                <InputLabel style={{transform: "translate(0, 1.5px) scale(0.75)"}}>Amount of Money</InputLabel>
                <input className={classes.input} onChange={moneyOnChange(setMoney)} value={money}/>
            </FormControl>

            <Button variant={"contained"} color={"primary"} className={classes.addButton} onClick={handleAdd(setAccounts, accounts, accountName)}>+</Button>
        </Container>
    )
}

const handleAdd = (setAccounts, accounts, accountName) => {
    setAccounts(accounts.concat([accountName.value, type, drcr]))
    alert(accounts)
}

const moneyOnChange = ({e, setMoney}) => {
    const re = /^[0-9\b]+$/
    if (e.target.value === '' || re.test(e.target.value)) {
        setMoney(e.target.value)
    }
}

function Accounts(props) {
    const [accountName, setAccountName] = React.useState("")
    const [type, setType] = React.useState("Asset")
    const [drcr, setdrcr] = React.useState("DR")
    const [money, setMoney] = React.useState("")


    return (
        <div style={{width: "80%", margin: 20}}>
            <Title titleName={"Accounts"}/>
            <AddingAccountForm
                accountName={accountName}
                setAccountNamme={setAccountName}
                type={type}
                setType={setType}
                drcr={drcr}
                setdrcr={setdrcr}
                money={money}
                setMoney={setMoney}
                setAccounts={props.setAccounts}
                accounts={props.accounts}
            />
        </div>
    )
}

export default Accounts
